import React, { Component } from 'react'
import _ from 'lodash'
import { Route } from 'react-router-dom'
import StepOne from './components/steps/step1'
import StepTwo from './components/steps/step2'
import StepThree from './components/steps/step3'
import StepFour from './components/steps/step4'
import StepFive from './components/steps/step5'
import connectComponent from '../../connect'
import { CreateCampaignAction } from '../../actions/CreateCampaign/emailCampaign'
import './index.scss'

class CreatePushNotifications extends Component {
    state = {
        step: 1,
        maxSteps: 5,
        CreateCampaignData: {},
    }
    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    // steps data
    _handleStepSubmit = params => {
        let { CreateCampaignData } = this.state
        CreateCampaignData = _.assign({}, CreateCampaignData, params)
        this.setState({
            CreateCampaignData,
        })
        // call api
        // CreateCampaignAction(CreateCampaignData)
    }

    render() {
        const { CreateCampaignData } = this.state
        const { history } = this.props
        return (
            <>
                <Route
                    exact
                    path={`${this.props.match.url}`}
                    render={() => (
                        <StepOne
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-2/`}
                    render={() => (
                        <StepTwo
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-3/`}
                    render={() => (
                        <StepThree
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-4/`}
                    render={() => (
                        <StepFour
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                        />
                    )}
                />
                <Route
                    exact
                    path={`${this.props.match.url}/step-5/`}
                    render={() => (
                        <StepFive
                            history={history}
                            data={CreateCampaignData}
                            onSubmit={this._handleStepSubmit}
                        />
                    )}
                />
            </>
        )
    }
}

export default connectComponent(CreatePushNotifications)
