import _ from 'lodash'
import Store from '@/reducer'
import Api from '@/utils/Api'
import * as Types from '@/constants/Types'
import history from '@/utils/history'

export const createParentId = async () => {
    try {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_LOADING,
        })
        // const response = await Api.createParent() // TODO: uncomment before build
        const response = { parentID: 34 }
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_SUCCESS,
            data: response,
        })
    } catch (e) {
        console.error(e)
    }
}

export const GetCampaign = async campaignID => {
    try {
        const response = await Api.GetCampaignAction(campaignID)
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_SUCCESS,
            data: response,
        })
        // console.log(response)
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
        })
    }
}

export const getParentDetails = async parentId => {
    try {
        const response = await Api.GetParentDetails(parentId)
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_SUCCESS,
            data: response,
        })
        console.log(response)
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
        })
    }
}

export const CreateCampaignAction = async ({ next, ...params }) => {
    Store.dispatch({
        type: Types.CREATE_CAMPAIGN_LOADING,
    })
    try {
        const response = await Api.CreateCampaignAction({ ...params })
        if (response) {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_UPDATE_DATA,
                data: _.assign({}, params, response),
            })
            history.push(`/email/${next}/${response.campaignId}`)
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}

export const CreateCampaignBulk = async (requests, next) => {
    try {
        requests = _.map(requests, part => {
            return Api.CreateCampaignAction(part)
        })
        const response = await Promise.all(requests)
        console.log(response, 'response of push in create')
        if (!response.error) {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_SUCCESS,
                data: response,
            })
            history.push(next)
        } else {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_FAILURE,
                data: response.error,
            })
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}

export const UpdateCampaignBulk = async (requests, next) => {
    try {
        requests = _.map(requests, part => {
            return Api.UpdateCampaign(part)
        })
        const response = await Promise.all(requests)
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_SUCCESS,
            data: response,
        })
        history.push(next)
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}

export const UpdateCampaignData = async ({ next, ...params }) => {
    console.log(params, 'params in updateå')
    try {
        const response = await Api.UpdateCampaign(params)
        if (response) {
            Store.dispatch({
                type: Types.CREATE_CAMPAIGN_UPDATE_DATA,
                data: _.assign({}, params, response),
            })
            history.push(`/email/${next}/${params.campaignId}`)
        }
    } catch (err) {
        Store.dispatch({
            type: Types.CREATE_CAMPAIGN_FAILURE,
            data: err.message,
        })
    }
}

export const UploadContent = async (file, category = 'email_template') => {
    try {
        const formData = new FormData()
        formData.append('file', file)
        formData.append('category', category)
        return await Api.CreateContent(formData)
    } catch (e) {
        console.error(e)
        return null
    }
}

export const uploadFile = async file => {
    await UploadContent(file)
}

export const SaveStep2 = async ({ htmlTemplate, ...rest }) => {
    const htmlBlob = new Blob([htmlTemplate], { type: 'plain/text' })
    const fileName =
        Math.random()
            .toString(36)
            .substr(2, 5) + '.html'
    const htmlFile = new File([htmlBlob], fileName)
    const res = await UploadContent(htmlFile)
    const { s3url } = res
    rest = _.assign({}, rest, { templatePath: s3url })
    UpdateCampaignData(rest)
}

export const SaveStep4 = async file => {
    const formData = new FormData()
    formData.append('input_file', file)
    const response = await UploadContent(file)
    console.log(response)
}
