import React, { Component } from 'react'
import WrapComponents from '@/components/wrapper'
import StepOne from './step1'
import StepTwo from './step2'
import { createPN } from '../../../../actions/addPNUser'
import './index.scss'

class CreatePushNotifications extends Component {
    state = {
        step: 1,
        maxSteps: 5,
        campaignName: '',
        data: '',
    }
    nextStep = () => {
        const { step } = this.state
        this.setState({
            step: step + 1,
        })
        console.log(step, 'step')
    }
    prevStep = () => {
        const { step } = this.state
        this.setState({
            step: step - 1,
        })
    }

    render() {
        const { step, data } = this.state
        console.log(step, 'steps in state')
        switch (step) {
            case 1:
                return (
                    <>
                        <StepOne
                            nextStep={this.nextStep}
                            data={this.state.data}
                            prevStep={this.prevStep}
                        />
                    </>
                )
            case 2:
                return <StepTwo nextStep={this.nextStep} prevStep={this.prevStep} />
            case 3:
                return <StepOne nextStep={this.nextStep} prevStep={this.prevStep} />
            case 4:
                return <StepOne nextStep={this.nextStep} prevStep={this.prevStep} />
            case 5:
                return <StepOne nextStep={this.nextStep} prevStep={this.prevStep} />
            default:
                return null
        }
    }
}

export default CreatePushNotifications
