import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { SmallHeading } from '@/components/headings'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import CodeCompaign from '../codeEditor'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview & Send'],
        html: true,
        content: false,
        subject: '',
        sender: '',
        senderError: '',
        replyTO: '',
        from: '',
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        const { subject, sender, replyTO, from } = this.state

        let anyError = false

        if (sender === '') {
            this.setState({
                senderError: true,
            })
            anyError = true
        }
        if (!anyError) {
            this.props.onSubmit({
                subject,
                sender,
                replyTO,
                from,
            })
        }
    }

    render() {
        const { steps, html, content, subject, sender, replyTO, from } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="section">
                    <SmallHeading>Compose Email</SmallHeading>
                    <div className="row">
                        <div className="col">
                            <h5>Email subject</h5>
                            <input
                                className="form-input"
                                placeholder="Enter Subject"
                                required
                                name={subject}
                                onChange={e => this.handleInputFields('subject', e.target.value)}
                            />
                        </div>
                        <div className="col">
                            <h5>Sender Name</h5>
                            <input
                                className="form-input"
                                placeholder="Enter Sender Name"
                                name={sender}
                                required
                                onChange={e => this.handleInputFields('sender', e.target.value)}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <h5>"From" email address</h5>
                            <input
                                className="form-input"
                                placeholder="Enter email address"
                                required
                                name={from}
                                onChange={e => this.handleInputFields('from', e.target.value)}
                            />
                        </div>
                        <div className="col">
                            <h5>"Reply-to" email address</h5>
                            <input
                                className="form-input"
                                placeholder="Enter email address"
                                required
                                name={replyTO}
                                onChange={e => this.handleInputFields('replyTO', e.target.value)}
                            />
                        </div>
                    </div>
                    <div className="flex">
                        <div
                            className={`type ${content ? 'active' : ''}`}
                            onClick={() => this.setState({ content: !content, html: !html })}
                        >
                            Design Template
                        </div>
                        <div
                            className={`type ${html ? 'active' : ''}`}
                            onClick={() => this.setState({ content: !content, html: !html })}
                        >
                            HTML Editor
                        </div>
                    </div>
                    {html ? (
                        <div className="mrgn-top-15 ">
                            <CodeCompaign />
                        </div>
                    ) : (
                        <div className="mrgn-top-15">content</div>
                    )}
                </div>
                <ColoredButtons as="link" to="/email/step-3" className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons className="float grey-border">Save as Draft</TransparentButtons>
            </WrapComponents>
        )
    }
}

export default StepTwo
