import React, { Component } from 'react'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'

class StepOne extends Component {
    state = {
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview'],
        name: '',
        devices: [
            {
                name: 'IOS',
                checked: false,
            },
            {
                name: 'Android',
                checked: false,
            },
            {
                name: 'Web',
                checked: false,
            },
            {
                name: 'Mobile',
                checked: false,
            },
            {
                name: 'Tablet',
                checked: false,
            },
        ],
        campaignType: [
            {
                name: 'General Campaign',
                checked: false,
            },
            {
                name: 'Trigger Campaign',
                checked: false,
            },
        ],
    }
    saveAndContinue = e => {
        e.preventDefault()
        this.props.nextStep()
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleCheckbox = check => {
        const { devices } = this.state
        this.setState({
            devices: devices.map(part => {
                if (part.name === check.name) {
                    part.checked = !check.checked
                }
                return part
            }),
        })
    }

    handleRadio = check => {
        const { devices } = this.state
        this.setState({
            devices: devices.map(part => {
                if (part.name === check.name) {
                    part.checked = true
                } else {
                    part.checked = false
                }
                return part
            }),
        })
    }

    render() {
        console.log(this.state, 'state')
        const { steps, devices, campaignType } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="section">
                    <SmallHeading>Campaign Name</SmallHeading>
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={this.state.name}
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Platforms</SmallHeading>
                    <div className="mrgn-top-15">
                        {devices.map(part => {
                            if (part.name === 'Web') {
                                return (
                                    <span key={part.name}>
                                        <Checkbox
                                            onClick={() => this.handleCheckbox(part)}
                                            name="device"
                                            checked={part.checked}
                                        >
                                            {part.name}
                                        </Checkbox>
                                        <div className="line">|</div>
                                    </span>
                                )
                            }
                            return (
                                <Checkbox
                                    key={part.name}
                                    onClick={() => this.handleCheckbox(part)}
                                    name="device"
                                    checked={part.checked}
                                >
                                    {part.name}
                                </Checkbox>
                            )
                        })}
                    </div>
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <div className="">
                        {campaignType.map(part => (
                            <Radio key={part.name} checked={part.checked}>
                                {part.ame}
                            </Radio>
                        ))}
                    </div>
                </div>
                <ColoredButtons onClick={this.saveAndContinue} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons onClick={this.back} className="float">
                    back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default StepOne
