import React, { Component } from 'react'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'
import { Radio } from '@/components/radio'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings/index'

class StepOne extends Component {
    state = {
        steps: ['Channel', 'Message', 'Segmentation', 'Scheduling', 'Overview'],
        broadcast: true,
        trigger: false,
        name: '',
        emailCampaignType: [
            {
                name: 'General Campaign',
                checked: true,
            },
            {
                name: 'Trigger Campaign',
                checked: false,
            },
        ],
        nameError: false,
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleRadio = check => {
        const { type } = this.state
        this.setState({
            type: type.map(part => {
                if (part.name === check.name) {
                    part.checked = true
                } else {
                    part.checked = false
                }
                return part
            }),
        })
    }

    handleContinue = () => {
        const { name, broadcast } = this.state

        let anyError = false

        if (name === '') {
            this.setState({
                nameError: true,
            })
            anyError = true
        }
        if (!anyError) {
            this.props.onSubmit({
                name,
                emailCampaignType,
            })
        }
    }
    render() {
        const { steps, name, nameError } = this.state
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="section">
                    <SmallHeading>Campaign Name</SmallHeading>
                    {nameError && <div className="alert">*This field is required</div>}
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={name}
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <div className="">
                        {emailCampaignType.map(part => (
                            <Radio
                                onClick={() => this.handleRadio(part)}
                                key={part.name}
                                checked={part.checked}
                                id={part.name}
                            >
                                {part.name}
                            </Radio>
                        ))}
                    </div>
                </div>
                <ColoredButtons onClick={this.handleContinue} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons onClick={this.back} className="float grey-border">
                    Save as Draft
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default StepOne
