import React, { Component } from 'react'
import Select from 'react-select'
import { ColoredButtons } from '@/components/button/index'
import './index.scss'
import { eventValues, operators, attributeNames } from './defaultData'
const selectTheme = theme => ({
    ...theme,
    borderRadius: 0,
    colors: {
        ...theme.colors,
        text: '#f1f1f1',
        primary25: '#74d69e',
        primary: '#74d69e',
    },
})

class CreateRuleImport extends Component {
    state = {
        addProperty: false,
        actionButtons: [
            {
                attribute: '',
                operator: '',
                value: '',
                canAddMore: true,
            },
        ],
    }

    handleAddPrimarySection = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            attribute: '',
            operator: '',
            value: '',
            canAddMore: false,
        })
    }

    handleRemovePrimarySection = index => {
        const { formData } = this.state
        const attributeSections = _.get(formData, 'primaryAction.attributeSection')
        // splice index from array
        attributeSections.splice(index, 1)
        // use handleOnChange function to setState
        this.handleOnChange('primaryAction.attributeSection', attributeSections)
    }

    render() {
        const { addProperty } = this.state
        return (
            <div className="section mrgn-top-15">
                <div
                    className="flex width-150"
                    style={{
                        alignItems: 'center',
                    }}
                >
                    <ColoredButtons onClick={() => this.setState({ addProperty: !addProperty })}>
                        Add Property
                    </ColoredButtons>
                    <ColoredButtons onClick={() => this.setState({ addProperty: !addProperty })}>
                        Add Actions
                    </ColoredButtons>
                    <Select className="width-150" placeholder="Select App" />
                </div>
                {addProperty ? (
                    <div key={1} className="flex-space-bt filter mrgn-top-15">
                        <Select
                            closeMenuOnSelect={true}
                            isMulti={false}
                            className="trigger-select"
                            options={attributeNames}
                            theme={selectTheme}
                            placeholder="Attribute Name"
                        />
                        <Select
                            closeMenuOnSelect={true}
                            isMulti={false}
                            className="trigger-select"
                            options={operators}
                            theme={selectTheme}
                            placeholder="Operator"
                        />
                        <Select
                            closeMenuOnSelect={true}
                            isMulti={false}
                            className="trigger-select"
                            options={eventValues}
                            theme={selectTheme}
                            placeholder="Value"
                        />
                        <div className="plus" onClick={this.handleAddPrimarySection}>
                            <img src={plusIcon} alt="" />
                        </div>
                        ) : (
                        <div
                            style={{ marginTop: '35px' }}
                            className="minus"
                            onClick={() => this.handleRemovePrimarySection(index)}
                        >
                            -
                        </div>
                    </div>
                ) : (
                    <>bye :) </>
                )}
            </div>
        )
    }
}

export default CreateRuleImport
