import React from 'react'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio/index'
import Select from 'react-select'
import { SmallHeading } from '@/components/headings/index'
import './index.scss'
import { UploadContent } from '@/actions/utils'
import Alert from '@/components/alert'
import plusIcon from '../../../../assets/images/add.svg'

class TabAndroid extends React.Component {
    state = {
        coupons: false,
        largeIcon: false,
        image: false,
        media: '',
        IconUrl: true,
        ImageUrl: true,
        upload: false,
        ImageUpload: false,
        SoundUrl: false,
        UploadIcon: false,
        notificationSound: '',
        sound: '',
        appLink: '',
        android: false,
        priority: 'high',
        description: '',
        NotificationSoundOptions: ['Default', 'iOS'],
        additionalPairs: [
            {
                key: '',
                value: '',
                canAddMore: true,
            },
        ],
    }
    handleInputFields(key, value) {
        if (this.props.onChange) {
            this.props.onChange(key, value)
        }
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    handleDripLevelChange = selected => {
        this.setState({
            notificationSound: selected,
        })
    }

    // push Additional data in additionalPairs
    _addPairs = () => {
        const { additionalPairs } = this.state
        additionalPairs.push({
            key: '',
            value: '',
            canAddMore: false,
        })
        this.setState({
            additionalPairs: additionalPairs.map((part, index) => {
                if (index === additionalPairs.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _handlePairsInput = (index, key, value) => {
        const { additionalPairs } = this.state
        additionalPairs[index][key] = value
        this.setState({ additionalPairs })
    }

    _removePairs = index => {
        const { additionalPairs } = this.state
        additionalPairs.splice(index, 1)
        this.setState({ additionalPairs })
    }

    handleFileChange = async e => {
        const { files } = e.target
        const res = await UploadContent(files[0], 'image')
        if (res) {
            this.setState({
                userFilePath: res.s3url,
                upload: true,
            })
        }
    }

    render() {
        const {
            largeIcon,
            image,
            IconUrl,
            ImageUrl,
            media,
            appLink,
            description,
            UploadIcon,
            ImageUpload,
            upload,
            NotificationSoundOptions,
        } = this.state

        return (
            <div className="section section-grid">
                <div className="advance-row">
                    <SmallHeading>Description </SmallHeading>
                    <div>
                        <label htmlFor="">Enter Description</label>
                        <textarea
                            type="text"
                            value={description}
                            onChange={e => {
                                this.handleInputFields('description', e.target.value)
                            }}
                            className="text-area"
                            placeholder="write description...."
                        />
                    </div>
                </div>
                <div className="advance-row">
                    <Checkbox
                        checked={largeIcon}
                        onChange={() => this.setState({ largeIcon: !largeIcon })}
                    >
                        Large Icon
                    </Checkbox>
                    {largeIcon && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            <Radio
                                checked={UploadIcon}
                                onClick={() =>
                                    this.setState({ IconUrl: !IconUrl, UploadIcon: !UploadIcon })
                                }
                            >
                                Upload Icon
                            </Radio>
                            <Radio
                                checked={IconUrl}
                                onClick={() =>
                                    this.setState({ IconUrl: !IconUrl, UploadIcon: !UploadIcon })
                                }
                            >
                                Enter URL
                            </Radio>
                            {IconUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        type="text"
                                        className="form-input"
                                        placeholder="Enter URL"
                                    />
                                </div>
                            )}
                            {!IconUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <div
                                        className="upload-file upload-box "
                                        onClick={this.handleFileChange}
                                    >
                                        <input
                                            type="file"
                                            className="file-input"
                                            id="uploadFile"
                                            onChange={this.handleFileChange}
                                            accept=".jpg"
                                        />
                                    </div>
                                </div>
                            )}
                        </div>
                    )}
                </div>
                <div className="advance-row">
                    <Checkbox checked={image} onChange={() => this.setState({ image: !image })}>
                        Image
                    </Checkbox>
                    {image && (
                        <div className="mrgn-top-15 margn-left-15 ">
                            <Radio
                                checked={ImageUpload}
                                onClick={() =>
                                    this.setState({
                                        ImageUrl: !ImageUrl,
                                        ImageUpload: !ImageUpload,
                                    })
                                }
                            >
                                Upload Image
                            </Radio>
                            <Radio
                                checked={ImageUrl}
                                onClick={() =>
                                    this.setState({
                                        ImageUrl: !ImageUrl,
                                        ImageUpload: !ImageUpload,
                                    })
                                }
                            >
                                Enter URL
                            </Radio>
                            {ImageUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <input
                                        name="media"
                                        type="text"
                                        value={media}
                                        className="form-input"
                                        placeholder="Enter URL"
                                        onChange={e =>
                                            this.handleInputFields('media', e.target.value)
                                        }
                                    />
                                </div>
                            )}
                            {!ImageUrl && (
                                <div className="mrgn-top-15 row-child">
                                    <div
                                        className="upload-file upload-box "
                                        onClick={this.handleFileChange}
                                    >
                                        <input
                                            type="file"
                                            className="file-input"
                                            id="uploadFile"
                                            onChange={this.handleFileChange}
                                            accept=".jpg"
                                        />
                                    </div>
                                </div>
                            )}
                            {upload && (
                                <Alert className="success">
                                    file has been uploaded successfully!
                                </Alert>
                            )}
                        </div>
                    )}
                </div>
                <div className="advance-row">
                    <SmallHeading>Select Notification Sound</SmallHeading>
                    <Select
                        className="city-select"
                        options={NotificationSoundOptions}
                        onChange={e => this.handleDripLevelChange(e.currentTarget.value)}
                        value={NotificationSoundOptions}
                    />
                </div>
                <div className="advance-row">
                    <SmallHeading>Notification Priority *</SmallHeading>
                    {/* <Radio>Default</Radio> */}
                    <Radio checked={true}>High</Radio>
                    {/* <Radio>Medium</Radio> */}
                </div>
                <div className="divider-line" />
                <div>
                    <SmallHeading>Actions *</SmallHeading>
                    <h6 className="mrgn-10">
                        Choose what happens when user clicks the body of this push notification.
                    </h6>
                    <div className="flex">
                        {/* <input className="form-input mrgn-10" placeholder="Deeplinking URL" /> */}
                        <input
                            name="appLink"
                            value={appLink}
                            onChange={e => this.handleInputFields('appLink', e.target.value)}
                            className="form-input mrgn-10"
                            placeholder="Enter Deeplinking URL"
                        />
                    </div>
                    <h6 className="mrgn-10">Additional Key Value pairs (Optional):</h6>
                    {this.state.additionalPairs.map((part, index) => (
                        <div key={index} className="flex-space-bt">
                            <input
                                value={part.key}
                                onChange={e => this._handlePairsInput(index, 'key', e.target.value)}
                                className="form-input mrgn-10"
                                placeholder="key"
                            />
                            <input
                                className="form-input mrgn-10"
                                value={part.value}
                                onChange={e =>
                                    this._handlePairsInput(index, 'value', e.currentTarget.value)
                                }
                                placeholder="value"
                            />
                            {part.canAddMore ? (
                                <div className="plus" onClick={this._addPairs}>
                                    <img src={plusIcon} alt="" />
                                </div>
                            ) : (
                                <div className="minus" onClick={() => this._removePairs(index)}>
                                    -
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        )
    }
}

