import React,{ Component } from 'react';
import {ColoredButtons} from '../../components/button'
import Header from '../../components/navbar'
import './index.scss'

class Login extends Component{
    state={
        email:'',
        password:''
    }
    onSubmit = (e) => {
        e.preventDefault();
    }

    handleInputFields(key,values,index){}

    render(){
        return(
            <div>
                {/* <Header/> */}
                <div className="content">
                    <div className="left-content"/>
                    <div className="right-content">
                    <div className="form-content">
                            <div className="header-image"/>
                            <div className="form-heading">
                                <div className="pt5 pb5"> Welcome back to IM Dashboard.</div>
                                <div className="pt5 pb5">Please Login to your account.</div>
                                <div className="read-more pt5 pb5">Learn More</div>
                            </div>
                            <div className="form">
                                <form  onSubmit={this.onSubmit} className="login-form">
                                    <input 
                                        placeholder="Email"
                                        type="text"
                                        className="form-input"
                                        name="email"
                                        value={this.state.email}
                                        onChange={e => this.handleInputFields('email', e.target.value)}
                                    />
                                    <input
                                        placeholder="Password"
                                        type="password"
                                        className="form-input"
                                        name="password"
                                        value={this.state.password}
                                        onChange={e => this.handleInputFields('password', e.target.value)}
                                    />
                                    <div className="flex"><span><input type="checkbox"/><span>ingat saya</span></span> <span>Lupa Kata Sandi?</span></div>
                                    <ColoredButtons onClick={this.onClick} className="lg-btn mrgn-bt-5 mrgn-tp-5">Masuk Ke Dashboard</ColoredButtons>
                                    <ColoredButtons  onClick={this.onClick} className="lg-trans-btn mrgn-tp-5">Masuk Dengan Google</ColoredButtons>
                                </form>
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}
export default Login;
