import React, { Component } from 'react'
import './index.scss'
import { Link } from 'react-router-dom'

class Sidebar extends Component {
    state = {
        open: true,
    }
    render() {
        const { isOpen, toggleSidebar } = this.props
        const loc = window.location.pathname
        const sidebarClass = isOpen ? 'Sidebar open' : 'Sidebar close'
        return (
            <div className="Wrapper">
                <div className={sidebarClass}>
                    <nav role="navigation">
                        <div className="cross" onClick={toggleSidebar}>
                            &times;
                        </div>
                        <ul className="sidebar-menu">
                            {!isOpen && (
                                <li className="Sidebar-navItem" onClick={toggleSidebar}>
                                    <div className="menu-strip" />
                                </li>
                            )}
                            {/* <li
                                className={`Sidebar-navItem ${
                                    loc === '/dashboard' ? 'active' : ''
                                }`}
                            >
                                <span className="Sidebar-menuIcon home" />
                                <Link to="/dashboard">Tokopedia Prod</Link>
                            </li> */}
                            <li
                                className={`Sidebar-navItem ${loc === '/campaign' ? 'active' : ''}`}
                            >
                                <i className="Sidebar-menuIcon campaign" />
                                <Link to="/campaign">Campaign</Link>
                            </li>
                            <li
                                className={`Sidebar-navItem ${
                                    loc === '/segmentation' ? 'active' : ''
                                }`}
                            >
                                <i className="Sidebar-menuIcon segment" />
                                <Link to="/segmentation">Segmentation</Link>
                            </li>
                            <li
                                className={`Sidebar-navItem ${loc === '/settings' ? 'active' : ''}`}
                            >
                                <i className="Sidebar-menuIcon setting" />
                                <Link to="/settings">Settings</Link>
                            </li>
                        </ul>
                    </nav>
                </div>
            </div>
        )
    }
}
export default Sidebar
