import React, { Component } from 'react'
import _ from 'lodash'
import './index.scss'
import { SmallHeading } from '@/components/headings'
import { Radio } from '@/components/radio'
import Select from 'react-select'
import plusIcon from '../../assets/images/add.svg'
import { ColoredButtons } from '../button/index'

import defaultData from './defaultData'

export const NotificationSoundOPtions = [{ value: 'Default', label: 'Default' }]
export const waitingOption = [
    { value: 'sec', label: 'Sec' },
    { value: 'min', label: 'Min' },
    { value: 'hour', label: 'Hour' },
    { value: 'month', label: 'Month' },
]

class TriggerCampaign extends Component {
    state = {
        actionButtons: [
            {
                attribute: '',
                operator: '',
                value: '',
                canAddMore: true,
            },
        ],
        addAction: true,
        addSecondary: false,
        waitTiming: false,
        time: 'min',
        timeCount: 30,
        primaryAction: true,
        occurance: '',
        formData: defaultData,
    }

    _addMoreActions = () => {
        const { actionButtons } = this.state
        actionButtons.push({
            text: '',
            appLink: '',
            canAddMore: false,
            icon: '',
        })
        this.setState({
            actionButtons: actionButtons.map((part, index) => {
                if (index === actionButtons.length - 1) {
                    part.canAddMore = true
                } else {
                    part.canAddMore = false
                }
                return part
            }),
        })
    }

    _removeAction = index => {
        const { actionButtons } = this.state
        actionButtons.splice(index, 1)
        this.setState({ actionButtons })
    }

    render() {
        const {
            actionButtons,
            addAction,
            addSecondary,
            waitTiming,
            time,
            timeCount,
            primaryAction,
            occurance,
            formData,
        } = this.state

        return (
            <>
                <SmallHeading>Primary Actions for Trigger Campaign</SmallHeading>
                <Select
                    className="width"
                    closeMenuOnSelect={false}
                    isMulti={false}
                    value={_.get(formData, 'primaryAction.event')}
                    options={_.get(formData, 'eventOptions')}
                    theme={theme => ({
                        ...theme,
                        borderRadius: 0,
                        colors: {
                            ...theme.colors,
                            text: '#f1f1f1',
                            primary25: '#74d69e',
                            primary: '#74d69e',
                        },
                    })}
                    placeholder="Select Event name"
                />
                {addAction && (
                    <>
                        {actionButtons.map((part, index) => (
                            <>
                                <div key={index} className="flex-space-bt filter mrgn-top-15">
                                    <Select
                                        className="trigger-select"
                                        value={part.attribute}
                                        // onChange={e =>
                                        //     this._handleActionsInput(
                                        //         index,
                                        //         'attribute',
                                        //         e.target.value
                                        //     )
                                        // }
                                        closeMenuOnSelect={true}
                                        isMulti={false}
                                        options={NotificationSoundOPtions}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Event name"
                                    />
                                    <Select
                                        className="trigger-select"
                                        value={part.operator}
                                        // onChange={e =>
                                        //     this._handleActionsInput(
                                        //         index,
                                        //         'operator',
                                        //         e.currentTarget.value
                                        //     )
                                        // }
                                        closeMenuOnSelect={true}
                                        isMulti={false}
                                        options={waitingOption}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Operator type"
                                    />
                                    <Select
                                        className="trigger-select"
                                        value={part.value}
                                        // onChange={e =>
                                        //     this._handleActionsInput(
                                        //         index,
                                        //         'value',
                                        //         e.currentTarget.value
                                        //     )
                                        // }
                                        closeMenuOnSelect={true}
                                        isMulti={false}
                                        options={NotificationSoundOPtions}
                                        theme={theme => ({
                                            ...theme,
                                            borderRadius: 0,
                                            colors: {
                                                ...theme.colors,
                                                text: '#f1f1f1',
                                                primary25: '#74d69e',
                                                primary: '#74d69e',
                                            },
                                        })}
                                        placeholder="Select Event name"
                                    />

                                    {part.canAddMore ? (
                                        <>
                                            {actionButtons.length < 6 ? (
                                                <div
                                                    className="plus"
                                                    onClick={this._addMoreActions}
                                                >
                                                    <img src={plusIcon} alt="" />
                                                </div>
                                            ) : (
                                                ''
                                            )}
                                        </>
                                    ) : (
                                        <div
                                            style={{ marginTop: '35px' }}
                                            className="minus"
                                            onClick={() => this._removeAction(index)}
                                        >
                                            -
                                        </div>
                                    )}
                                </div>
                            </>
                        ))}
