import React, { Component, createRef } from 'react'
import _ from 'lodash'
import history from '@/utils/history'
import Stepper from '@/components/stepper'
import ConnectComponent from '../../../../connect'
import WrapComponents from '@/components/wrapper'
import Alert from '@/components/alert'
import Loader from '@/components/loader'
import androidMobile from '../../../../assets/images/andriod.png'
import { SmallHeading } from '@/components/headings'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { GetCampaign, SaveStep2 } from '@/actions/CreateCampaign/emailCampaign'
import './index.scss'
import 'emoji-mart/css/emoji-mart.css'
import { Picker } from 'emoji-mart'

class StepTwo extends Component {
    state = {
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        message: '',
        messageError: false,
        campaignId: '',
        emojiShow: false,
        textCount: 0,
        base: 160,
    }

    messageInput = createRef()

    async componentDidMount() {
        const {
            params: { campaignId },
        } = this.props.match
        this.setState({
            campaignId,
        })
        const res = await GetCampaign(campaignId)
        this.setState({
            message: _.get(res, 'message', ''),
        })
    }

    handleInputFields(key, value) {
        this.setState({
            [key]: value,
        })
    }

    handleContinue = () => {
        const { message } = this.state
        let anyError = false
        if (message.trim() === '') {
            this.setState({
                messageError: true,
            })
            anyError = true
        }
        if (!anyError) {
            const { steps, campaignId, messageError, text, emojiShow, ...rest } = this.state
            const { data } = this.props.CreateCampaign
            SaveStep2({ ..._.assign({}, data, rest) })
            history.push(`/sms/step-3/${campaignId}`)
        }
    }

    handleChange = e => {
        let firstBase = 160,
            inputValue = e.target.value
        if (inputValue.length === 0) {
            this.setState({ message: inputValue, textCount: 0, base: firstBase })
        } else if (inputValue.length < firstBase) {
            this.setState({ message: inputValue, textCount: 1, base: firstBase })
        } else {
            this.updateCounter(inputValue, firstBase)
        }
    }

    updateCounter = (inputValue, firstBase) => {
        let secondBase = 140,
            length = inputValue.length - firstBase,
            noOfSms = 2
        noOfSms = noOfSms + Math.floor(length / secondBase)
        this.setState({ message: inputValue, textCount: noOfSms, base: secondBase })
    }

    addEmoji = e => {
        if (e.unified.length <= 5) {
            let emojiPic = String.fromCodePoint(`0x${e.unified}`)
            this.setState({
                message: this.state.message + emojiPic,
            })
        } else {
            let sym = e.unified.split('-')
            let codesArray = []
            sym.forEach(el => codesArray.push('0x' + el))
            let emojiPic = String.fromCodePoint(...codesArray)
            this.setState({
                message: this.state.message + emojiPic,
            })
        }
        if (this.messageInput) {
            this.messageInput.focus()
        }
    }
    render() {
        const { steps, messageError, textCount, emojiShow, base, message } = this.state
        const { error, loading } = this.props.CreateCampaign
        if (loading) {
            return <Loader />
        }

        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={1} />
                <div className="flex-space-bt ">
                    <div className="section-left">
                        <SmallHeading>Create Message</SmallHeading>
                        {messageError && <Alert className="alert">*This field is Required</Alert>}
                        {error && <Alert className="alert">*This field is Required</Alert>}
                        <input
                            ref={r => (this.messageInput = r)}
                            type="text"
                            className="text-area"
                            value={this.state.message}
                            onChange={this.handleChange}
                            placeholder="Please type your message here....."
                        />
                        <div className="flex-space-bt margin-btm">
                            <span className="text-count">
                                {textCount}/{base}
                            </span>
                            <div
                                className="emoji-icon"
                                onClick={() => this.setState({ emojiShow: !emojiShow })}
                            >
                                <svg
                                    xmlns="http://www.w3.org/2000/svg"
                                    viewBox="0 0 24 24"
                                    width="24"
                                    height="24"
                                >
                                    <path d="M12 0C5.373 0 0 5.373 0 12s5.373 12 12 12 12-5.373 12-12S18.627 0 12 0m0 22C6.486 22 2 17.514 2 12S6.486 2 12 2s10 4.486 10 10-4.486 10-10 10" />
                                    <path d="M8 7a2 2 0 1 0-.001 3.999A2 2 0 0 0 8 7M16 7a2 2 0 1 0-.001 3.999A2 2 0 0 0 16 7M15.232 15c-.693 1.195-1.87 2-3.349 2-1.477 0-2.655-.805-3.347-2H15m3-2H6a6 6 0 1 0 12 0" />
                                </svg>
                            </div>
                        </div>
                        {emojiShow ? (
                            <div className="emoji-container">
                                <Picker onSelect={this.addEmoji} />
                            </div>
                        ) : null}
                        <ColoredButtons onClick={this.handleContinue} className="float">
                            Continue
                        </ColoredButtons>
                        <TransparentButtons
                            onClick={() => this.props.history.goBack()}
                            className="float grey-border"
                        >
                            Go Back
                        </TransparentButtons>
                    </div>
                    <div className="section section-right">
                        <SmallHeading>Preview</SmallHeading>
                        <div className="preview-text">
                            Actual display may be different to this preview depending on the user’s
                            device.
                        </div>
                        <div
                            className="preview-container"
                            style={{ backgroundImage: `url(${androidMobile})` }}
                        >
                            <div className="preview-wrap android-main">
                                <div className="preview-content">
                                    <div className="preview-header">Tokopedia • Summary • Now</div>
                                    <div className="row">
                                        <div className="col-8">
                                            <div className="preview-msg">{message}</div>
                                        </div>
                                        <div className="col-4 text-right">
                                            <div className="pn-image" />
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </WrapComponents>
        )
    }
}

export default ConnectComponent(StepTwo)
