import React, { Component } from 'react'
import _ from 'lodash'
import connectComponent from '@/connect'
import { ColoredButtons, TransparentButtons } from '@/components/button'
import { SmallHeading } from '@/components/headings'
import { Checkbox } from '@/components/checkBox'
import { Radio } from '@/components/radio'
import Stepper from '@/components/stepper'
import WrapComponents from '@/components/wrapper'

class StepOne extends Component {
    state = {
        name: '',
        nameError: false,
        steps: ['Channel', 'Message', 'Scheduling', 'Segmentation', 'Overview'],
        deviceTypeError: false,
        android: false,
        ios: false,
        web: false,
        generalCampaing: true,
    }

    handleContinue = () => {
        const { name, android, web, ios, type } = this.state

        let anyError = false

        if (name === '') {
            this.setState({
                nameError: true,
            })
            anyError = true
        }

        if (!anyError) {
            this.props.onSubmit({
                name,
                type,
            })
        }
    }

    back = e => {
        e.preventDefault()
        this.props.prevStep()
    }

    handleInputFields(key, value) {
        if (!value) {
            this.setState({
                [key]: value,
            })
        } else {
            this.setState({
                [key]: value,
            })
        }
    }

    render() {
        const { steps, deviceTypeError, nameError, android, ios, web, generalCampaing } = this.state
        console.log(deviceType, 'deviceType')
        return (
            <WrapComponents>
                <Stepper steps={steps} activeStep={0} />
                <div className="section">
                    {nameError && <div className="alert">*this is mandatory field</div>}
                    <SmallHeading>Campaign Name*</SmallHeading>
                    <input
                        placeholder="Enter Campaign Name"
                        type="text"
                        className="form-input"
                        name="name"
                        value={this.state.name}
                        onChange={e => this.handleInputFields('name', e.target.value)}
                    />
                </div>
                <div className="section">
                    <SmallHeading>Platforms*</SmallHeading>
                    {deviceTypeError && <div className="alert">*this is mandatory field</div>}
                    <div className="mrgn-top-15">
                        <Checkbox
                            onClick={() => this.setState({ android: !android })}
                            name="android"
                            checked={android}
                        >
                            Android
                        </Checkbox>
                        <Checkbox
                            onClick={() => this.setState({ ios: !ios })}
                            name="ios"
                            checked={ios}
                        >
                            IOS
                        </Checkbox>
                        <Checkbox
                            onClick={() => this.setState({ web: !web })}
                            name="web"
                            checked={web}
                        >
                            Web
                        </Checkbox>
                    </div>
                </div>
                <div className="section">
                    <SmallHeading>Campaign Type</SmallHeading>
                    <div className="">
                        <Radio checked={generalCampaing}>General Campaign</Radio>
                    </div>
                </div>
                <ColoredButtons onClick={() => this.handleContinue} className="float">
                    Continue
                </ColoredButtons>
                <TransparentButtons
                    onClick={() => this.props.history.goBack()}
                    className="float grey-border"
                >
                    Go Back
                </TransparentButtons>
            </WrapComponents>
        )
    }
}

export default connectComponent(StepOne)
